
public class Caso1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("WHILE - Contador ascendente");
		System.out.println("---------------------------");
		
		int x = 1;
		
		while (x <= 5) {
			System.out.println("Bienvenido " + x);
			x++;
		}

	}

}
